import axios from "axios";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {

  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5000/blogs")
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          setBlogs(response.data.slice(0, 2)); // Show only the first 2 blogs
        }
      })
      .catch((error) => console.error("Error fetching blogs:", error))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div className="text-center p-8 text-lg font-semibold">Loading blogs...</div>;
  }

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        <div className="">
          <h2 className="">Latest Blogs</h2>


          <div class="blogrightsec">

            {/* <div class="blogs">
              <div class="bimg">
                <img src="/static/media/parallax-2.ee165fd0145bda0060b0.jpg" alt="" />
              </div>
              <div class="bdesc">
                <p class="orther">BY <span>RAM </span> | Jun 18, 2015</p>
                <h1>Integer at diam gravida</h1>
                <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nobis, dolor.</p>
                <button>Read More <i class="fa-solid fa-arrow-right"></i></button>
              </div>
            </div> */}

            {blogs.map((blog) => (
              <div key={blog.id} className="bg-white p-4 rounded-lg shadow-md">
                <img src={blog.image} alt={blog.title} className="w-full h-64 object-cover rounded-md" />
                <div className="p-4">
                  <p className="text-gray-600 text-sm">By <span className="font-semibold">{blog.author}</span> | {blog.date}</p>
                  <h3 className="text-xl font-semibold mt-2">{blog.title}</h3>
                  <p className="text-gray-700 mt-2">{blog.description.slice(0, 100)}...</p>
                  <Link to={`/blog/${blog.id}`} className="text-blue-500 font-semibold mt-4 inline-block">
                    Read More â†’
                  </Link>
                </div>
              </div>
            ))}



            <Link to="/blogs" className="">Show More Blogs</Link>
          </div>
        </div>
      </div>

    </div>
  );
};









export default BlogSection;
