import React, { useState } from "react";
import axios from "axios";

const BlogForm = () => {
  const [blogData, setBlogData] = useState({ author: "", title: "", content: "", image: null });

  // Handle input changes for blog submission
  const handleChange = (e) => {
    if (e.target.name === "image") {
      setBlogData({ ...blogData, image: e.target.files[0] });
    } else {
      setBlogData({ ...blogData, [e.target.name]: e.target.value });
    }
  };

  // Handle Blog Submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("author", blogData.author);
    formData.append("title", blogData.title);
    formData.append("content", blogData.content);
    if (blogData.image) formData.append("image", blogData.image);

    try {
      await axios.post("http://localhost:5000/api/blogs", formData);
      alert("Blog added successfully");
    } catch (error) {
      console.error("Error adding blog", error);
    }
  };

  return (
    <div className="p-4 max-w-md mx-auto bg-white rounded-lg shadow-md">
      <form onSubmit={handleSubmit}>
        <h2 className="text-xl font-bold mb-4">Add Blog</h2>
        <input className="w-full p-2 mb-2 border rounded" type="text" name="author" placeholder="Author" onChange={handleChange} />
        <input className="w-full p-2 mb-2 border rounded" type="text" name="title" placeholder="Title" onChange={handleChange} />
        <textarea className="w-full p-2 mb-2 border rounded" name="content" placeholder="Content" onChange={handleChange}></textarea>
        <input className="w-full p-2 mb-2 border rounded" type="file" name="image" onChange={handleChange} />
        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Submit</button>
      </form>
    </div>
  );
};

export default BlogForm;
