import React, { useState, useEffect } from "react";
import "./BlogSection.css";

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);
  const [activeBlog, setActiveBlog] = useState(null);

  // Fetch blogs from JSON Server
  useEffect(() => {
    fetch("http://localhost:5000/blogs")
      .then((response) => response.json())
      .then((data) => setBlogs(data))
      .catch((error) => console.error("Error fetching blogs:", error));
  }, []);

  // Function to handle "Read More" click
  const handleReadMore = (blogId) => {
    setActiveBlog(activeBlog === blogId ? null : blogId); // Toggle blog view
  };

  return (
    <div className="blogsec">
      <div className="blogrightsec">
        {blogs.map((blog) => (
          <div key={blog.id} className="blogs">
            <div className="bimg">
              <img src={require(`../assets/${blog.image}`)} alt={blog.title} />
            </div>
            <div className="bdesc">
              <p className="orther">
                BY <span>{blog.author}</span> | {blog.date}
              </p>
              <h1>{blog.title}</h1>
              <p>
                {activeBlog === blog.id ? blog.content : blog.excerpt}
              </p>
              <button onClick={() => handleReadMore(blog.id)}>
                {activeBlog === blog.id ? "Show Less" : "Read More"}{" "}
                <i className="fa-solid fa-arrow-right"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogSection;
