import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const BlogDetails = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [comment, setComment] = useState("");
  const [userId] = useState("user123"); // Replace with actual user ID

  useEffect(() => {
    axios.get(`http://localhost:5001/blogs/${id}`).then((res) => {
      setBlog(res.data);
    });
  }, [id]);

  const handleLike = async () => {
    try {
      const res = await axios.post(`http://localhost:5001/blogs/${id}/like`, { userId });
      setBlog((prev) => ({ ...prev, likes: res.data.likes }));
    } catch (error) {
      console.error("Error liking blog:", error);
    }
  };

  const handleCommentSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post(`http://localhost:5001/blogs/${id}/comment`, {
        username: "Guest",
        text: comment,
      });
      setBlog((prev) => ({ ...prev, comments: res.data.comments }));
      setComment("");
    } catch (error) {
      console.error("Error adding comment:", error);
    }
  };

  if (!blog) return <div className="text-center mt-10">Loading...</div>;

  return (
    <div className="max-w-3xl mx-auto p-5 bg-white shadow-lg rounded-lg mt-5">
      <h1 className="text-3xl font-bold">{blog.title}</h1>
      <p className="text-gray-600">by {blog.author}</p>
      {blog.image && <img src={`http://localhost:5001${blog.image}`} alt="Blog" className="w-full mt-4 rounded-lg" />}

      {/* Render Dynamic Content */}
      <div className="mt-4">
        {blog.content.map((item, index) =>
          item.type === "heading" ? (
            <h2 key={index} className="text-xl font-semibold mt-4">{item.value}</h2>
          ) : (
            <p key={index} className="text-gray-700 mt-2">{item.value}</p>
          )
        )}
      </div>

      {/* Like and Comment Section */}
      <div className="mt-5">
        <button
          className="bg-red-500 text-white px-4 py-2 rounded"
          onClick={handleLike}
        >
          ❤️ Like {blog.likes}
        </button>

        <h3 className="mt-4 text-lg font-semibold">Comments</h3>
        <form onSubmit={handleCommentSubmit} className="mt-2">
          <input
            type="text"
            placeholder="Add a comment..."
            className="w-full p-2 border rounded"
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            required
          />
          <button type="submit" className="bg-blue-500 text-white px-4 py-2 mt-2 rounded">
            Comment
          </button>
        </form>

        <ul className="mt-4">
          {blog.comments.map((c, index) => (
            <li key={index} className="border-b py-2">
              <strong>{c.username}:</strong> {c.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default BlogDetails;
