import { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const BlogForm = () => {
    const navigate = useNavigate();
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [image, setImage] = useState(null);
    const [sections, setSections] = useState([{ heading: '', paragraph: '' }]);
    const [loading, setLoading] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const formData = new FormData();
            formData.append('title', title);
            formData.append('author', author);
            formData.append('image', image);
            formData.append('sections', JSON.stringify(sections));

            await axios.post('http://localhost:5001/blogs', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });

            alert('Blog uploaded successfully!');

            // Redirect to homepage after successful upload
            navigate('/');
        } catch (error) {
            console.error('Error uploading blog:', error);
            alert('Failed to upload blog.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="max-w-2xl mx-auto p-6 bg-white shadow-lg rounded-lg">
            <h2 className="text-2xl font-bold mb-4">Upload Blog</h2>
            <form onSubmit={handleSubmit} className="space-y-4">
                <input 
                    type="text" 
                    placeholder="Title" 
                    value={title} 
                    onChange={(e) => setTitle(e.target.value)} 
                    className="w-full p-2 border rounded"
                    required
                />
                <input 
                    type="text" 
                    placeholder="Author" 
                    value={author} 
                    onChange={(e) => setAuthor(e.target.value)} 
                    className="w-full p-2 border rounded"
                    required
                />
                <input 
                    type="file" 
                    onChange={(e) => setImage(e.target.files[0])} 
                    className="w-full p-2 border rounded"
                    required
                />

                {sections.map((section, index) => (
                    <div key={index} className="space-y-2">
                        <input 
                            type="text" 
                            placeholder="Heading" 
                            value={section.heading} 
                            onChange={(e) => {
                                const newSections = [...sections];
                                newSections[index].heading = e.target.value;
                                setSections(newSections);
                            }} 
                            className="w-full p-2 border rounded"
                        />
                        <textarea 
                            placeholder="Paragraph" 
                            value={section.paragraph} 
                            onChange={(e) => {
                                const newSections = [...sections];
                                newSections[index].paragraph = e.target.value;
                                setSections(newSections);
                            }} 
                            className="w-full p-2 border rounded"
                        />
                    </div>
                ))}

                <button 
                    type="submit" 
                    className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600 transition"
                    disabled={loading}
                >
                    {loading ? 'Uploading...' : 'Upload Blog'}
                </button>
            </form>
        </div>
    );
};

export default BlogForm;
