import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BlogDetail = () => {
    const { id } = useParams();
    const [blog, setBlog] = useState(null);
    const [comment, setComment] = useState('');
    const [comments, setComments] = useState([]);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/api/blogs/${id}`);
                setBlog(response.data);
                setComments(response.data.comments || []);
            } catch (error) {
                console.error('Error fetching blog:', error);
            }
        };
        fetchBlog();
    }, [id]);

    const handleCommentSubmit = async () => {
        if (!comment.trim()) return;
        try {
            const response = await axios.post(`http://localhost:5001/api/blogs/${id}/comments`, { text: comment });
            setComments([...comments, response.data]);
            setComment('');
        } catch (error) {
            console.error('Error posting comment:', error);
        }
    };

    if (!blog) return <p>Loading...</p>;

    return (
        <div className="p-6 max-w-3xl mx-auto">
            <h2 className="text-2xl font-bold mb-2">{blog.title}</h2>
            <p className="text-gray-600 mb-4">By {blog.author}</p>
            {blog.image && <img src={`http://localhost:5001${blog.image}`} alt={blog.title} className="w-full h-64 object-cover rounded" />}
            {blog.sections.map((section, index) => (
                <div key={index} className="mt-4">
                    <h3 className="text-lg font-semibold">{section.heading}</h3>
                    <p>{section.paragraph}</p>
                </div>
            ))}
            <div className="mt-6">
                <h3 className="text-lg font-semibold">Comments</h3>
                <div className="mt-2">
                    {comments.map((c, index) => (
                        <p key={index} className="border-b py-2">{c.text}</p>
                    ))}
                </div>
                <input type="text" value={comment} onChange={(e) => setComment(e.target.value)} placeholder="Add a comment" className="border p-2 w-full mt-2" />
                <button onClick={handleCommentSubmit} className="bg-blue-500 text-white px-4 py-2 rounded mt-2">Submit</button>
            </div>
        </div>
    );
};

export default BlogDetail;
