import React, { useState, useEffect } from 'react';
import './BlogSection.css';

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    // Fetch the list of blogs from the JSON server
    const fetchBlogs = async () => {
      try {
        const response = await fetch('http://localhost:5000/blogs');
        const data = await response.json();
        setBlogs(data); // Set the blog data in the state
      } catch (error) {
        console.error('Error fetching blogs:', error);
      }
    };

    fetchBlogs();
  }, []);

  // Function to handle opening the full blog in a new window
  const openBlogInNewWindow = (id) => {
    const blogUrl = `/blog/${id}`; // Construct the URL for the full blog post
    window.open(blogUrl, '_blank'); // Open in a new window or tab
  };

  return (
    <div className="blogsec">
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        {blogs.map((blog) => (
          <div className="blogs" key={blog.id}>
            <div className="bimg">
              <img src={blog.image} alt={blog.title} />
            </div>
            <div className="bdesc">
              <p className="orther">
                BY <span>{blog.author}</span> | {blog.date}
              </p>
              <h1>{blog.title}</h1>
              <p>{blog.content.slice(0, 100)}...</p>
              {/* Use the function to open the blog in a new window */}
              <button onClick={() => openBlogInNewWindow(blog.id)}>
                Read More <i className="fa-solid fa-arrow-right"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogSection;
