import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";
import axios from "axios";

const Home = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/blogs").then((response) => {
      setBlogs(response.data.slice(0, 3)); // Show only 2-3 blogs initially
    });
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">Latest Blogs</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {blogs.map((blog) => (
          <div key={blog.id} className="border rounded-lg p-4 shadow-md">
            <img src={blog.image} alt={blog.title} className="w-full h-48 object-cover mb-4" />
            <h2 className="text-xl font-semibold">{blog.title}</h2>
            <p className="text-gray-600">{blog.excerpt}</p>
            <Link to={`/blog/${blog.id}`} className="text-blue-500 mt-2 block">Read More</Link>
          </div>
        ))}
      </div>
      <Link to="/blogs" className="mt-6 block text-center text-white bg-blue-500 py-2 px-4 rounded">Show More Blogs</Link>
    </div>
  );
};

const BlogList = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/blogs").then((response) => {
      setBlogs(response.data);
    });
  }, []);

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">All Blogs</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {blogs.map((blog) => (
          <div key={blog.id} className="border rounded-lg p-4 shadow-md">
            <img src={blog.image} alt={blog.title} className="w-full h-48 object-cover mb-4" />
            <h2 className="text-xl font-semibold">{blog.title}</h2>
            <p className="text-gray-600">{blog.excerpt}</p>
            <Link to={`/blog/${blog.id}`} className="text-blue-500 mt-2 block">Read More</Link>
          </div>
        ))}
      </div>
    </div>
  );
};

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/blogs/${id}`).then((response) => {
      setBlog(response.data);
    });
  }, [id]);

  if (!blog) return <p>Loading...</p>;

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold">{blog.title}</h1>
      <img src={blog.image} alt={blog.title} className="w-full h-64 object-cover my-4" />
      <p>{blog.content}</p>
      <Link to="/" className="text-blue-500 block mt-4">Back to Home</Link>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/blogs" element={<BlogList />} />
        <Route path="/blog/:id" element={<BlogDetail />} />
      </Routes>
    </Router>
  );
};

export default App;
