import React, { useState, useEffect } from 'react';
import './BlogSection.css';
import { Link } from 'react-router-dom';

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    // Fetch the list of blogs from your API
    const fetchBlogs = async () => {
      try {
        const response = await fetch('/api/blogs');
        const data = await response.json();
        setBlogs(data); // Save the fetched data to state
      } catch (error) {
        console.error('Error fetching blogs:', error);
      }
    };

    fetchBlogs();
  }, []);

  return (
    <div className="blogsec">
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        {blogs.map((blog) => (
          <div className="blogs" key={blog.id}>
            <div className="bimg">
              {/* Optionally, you can add a placeholder image for the blog */}
              <img src="path_to_placeholder_image.jpg" alt="" />
            </div>
            <div className="bdesc">
              <p className="orther">
                BY <span>{blog.author}</span> | {blog.date}
              </p>
              <h1>{blog.title}</h1>
              <p>{blog.content.slice(0, 100)}...</p>
              <Link to={`/blog/${blog.id}`}>
                <button>Read More <i className="fa-solid fa-arrow-right"></i></button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogSection;
