import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/blogs")
      .then((response) => setBlogs(response.data))
      .catch((error) => console.error("Error fetching blogs:", error));
  }, []);

  return (
    <div className="blogs-container">
      <h2 className="text-2xl font-bold text-center mb-6">All Blogs</h2>
      {blogs.length > 0 ? (
        blogs.map((blog) => (
          <div key={blog._id} className="blog border p-4 mb-4 rounded-md">
            {blog.image && <img src={`http://localhost:5000${blog.image}`} alt={blog.title} className="w-full h-auto mb-2" />}
            <h3 className="text-xl font-semibold">{blog.title}</h3>
            <p>{blog.content.substring(0, 100)}...</p>
            <Link to={`/blog/${blog._id}`} className="text-blue-500 font-semibold mt-4 inline-block">
              Read More â†’
            </Link>
          </div>
        ))
      ) : (
        <p className="text-center text-gray-600">No blogs available.</p>
      )}
    </div>
  );
};

export default Blogs;
