import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function BlogDetail() {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [userId, setUserId] = useState('12345'); // Example user ID (replace with auth system)
  const [commentText, setCommentText] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/blogs/${id}`).then((response) => {
      setBlog(response.data);
    });
  }, [id]);

  const handleLike = async () => {
    try {
      const response = await axios.post(`http://localhost:5000/like/${id}`, { userId });
      setBlog({ ...blog, likes: response.data.likes });
    } catch (error) {
      alert('Error liking blog');
    }
  };

  const handleComment = async () => {
    if (!commentText) return;
    try {
      const response = await axios.post(`http://localhost:5000/comment/${id}`, {
        user: 'Anonymous',
        text: commentText,
      });
      setBlog({ ...blog, comments: response.data.comments });
      setCommentText('');
    } catch (error) {
      alert('Error adding comment');
    }
  };

  if (!blog) return <div>Loading...</div>;

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white shadow rounded-lg">
      <h1 className="text-2xl font-bold mb-2">{blog.title}</h1>
      <p className="text-gray-600 mb-2">By {blog.author} - {new Date(blog.createdAt).toLocaleString()}</p>
      {blog.image && <img src={`http://localhost:5000/${blog.image}`} alt="Blog" className="w-full rounded mb-4" />}
      {blog.content.map((section, index) => (
        <div key={index} className="mb-4">
          <h2 className="text-xl font-semibold">{section.heading}</h2>
          <p>{section.paragraph}</p>
        </div>
      ))}
      <button onClick={handleLike} className="bg-blue-500 text-white p-2 rounded mb-4">
        Like ({blog.likes})
      </button>
      <div className="mt-4">
        <h3 className="text-lg font-semibold">Comments</h3>
        {blog.comments.map((comment, index) => (
          <div key={index} className="p-2 border-b">
            <p className="text-sm font-bold">{comment.user}</p>
            <p>{comment.text}</p>
          </div>
        ))}
        <input
          type="text"
          value={commentText}
          onChange={(e) => setCommentText(e.target.value)}
          placeholder="Add a comment..."
          className="w-full p-2 border rounded mt-2"
        />
        <button onClick={handleComment} className="bg-green-500 text-white p-2 rounded mt-2">Submit</button>
      </div>
    </div>
  );
}
