import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './BlogSection.css';

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5000/blogs")
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          setBlogs(response.data.slice(0, 2)); // Show only the first 2 blogs
        }
      })
      .catch((error) => console.error("Error fetching blogs:", error))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div className="text-center p-8 text-lg font-semibold">Loading blogs...</div>;
  }

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        <h2 className="text-2xl font-bold text-center">Latest Blogs</h2>



        {blogs.length > 0 ? (
          blogs.map((blog) => (
            <div key={blog.id} class="blogs">
              <div class="bimg">
                {blog.image && <img src={blog.image} alt={blog.title} className="" />}

              </div>
              <div class="bdesc">
                <p class="orther">BY <span>{blog.auther}</span> | {blog.date}</p>
                <h1>{blog.title}</h1>
                <p>{blog.excerpt ? blog.excerpt.slice(0, 100) + "..." : "No description available."}</p>
                <Link to={`/blog/${blog.id}`} className="text-blue-500 font-semibold mt-4 inline-block">
                  Read More â†’
                </Link>
              </div>
            </div>

          ))
        ) : (
          <p className="text-center col-span-2 text-gray-600">No blogs available.</p>
        )}

      </div>
      <div className="text-center mt-6">
        <Link to="/blogs" className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-md hover:bg-blue-600">
          Show More Blogs
        </Link>
      </div>
    </div>

  );
};

export default BlogSection;
