import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BlogList = () => {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await axios.get('http://localhost:5001/blogs');
                setBlogs(response.data);
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };
        fetchBlogs();
    }, []);

    return (
        <div className="p-6 max-w-4xl mx-auto">
            <h2 className="text-xl font-bold mb-4">Blog List</h2>
            <div className="grid gap-4">
                {blogs.map((blog) => (
                    <div key={blog._id} className="p-4 border rounded shadow">
                        <h3 className="text-lg font-semibold">{blog.title}</h3>
                        <p className="text-gray-600">By {blog.author}</p>
                        {blog.image && <img src={`http://localhost:5001${blog.image}`} alt={blog.title} className="mt-2 w-full h-48 object-cover rounded" />}
                        <p className="mt-2">{blog.sections[0]?.paragraph.slice(0, 100)}...</p>
                        <Link to={`/blogs/${blog._id}`} className="text-blue-500 mt-2 block">Read More</Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
