import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/blogs")
      .then((response) => setBlogs(response.data))
      .catch((error) => console.error("Error fetching blogs:", error));
  }, []);

  return (
    <div className="blogs-container">
      <h2 className="text-2xl font-bold text-center mb-6">All Blogs</h2>

      {/*  */}






      {blogs.length > 0 ? (
        blogs.map((blog) => (
         
      <div key={blog._id} class="max-w-sm bg-white border border-gray-200 rounded-lg shadow-sm dark:bg-gray-800 dark:border-gray-700">
      <a href="#">
        {blog.image && <img src={`http://localhost:5000${blog.image}`} alt={blog.title} className="w-full h-auto mb-2" />}
      </a>
      <div class="p-5">
        <a href="#">
          <h5 class="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{blog.title}</h5>
        </a>
        <p class="mb-3 font-normal text-gray-700 dark:text-gray-400">{blog.content.substring(0, 100)}...</p>
        <Link to={`/blog/${blog._id}`} className="text-blue-500 font-semibold mt-4 inline-block">
          Read More  <svg class="rtl:rotate-180 w-3.5 h-3.5 ms-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
            <path stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
          </svg>
        </Link>


      </div>
    </div>
        ))
      ) : (
        <p className="text-center text-gray-600">No blogs available.</p>
      )}
    </div>
  );
};

export default Blogs;
