import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const BlogList = () => {
    const [blogs, setBlogs] = useState([]);

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await axios.get('http://localhost:5001/blogs');
                setBlogs(response.data);
            } catch (error) {
                console.error('Error fetching blogs:', error);
            }
        };
        fetchBlogs();
    }, []);

    return (
        <div className="p-6 max-w-6xl mx-auto">
            <h2 className="text-3xl font-bold text-gray-800 text-center mb-6">Latest Blogs</h2>
            
            {/* Blog Grid Layout */}
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {blogs.map((blog) => (
                    <div key={blog._id} className="bg-white rounded-lg shadow-lg overflow-hidden transition-transform transform hover:scale-105">
                        {/* Blog Image */}
                        {blog.image && (
                            <img 
                                src={`http://localhost:5001${blog.image}`} 
                                alt={blog.title} 
                                className="w-full h-52 object-cover"
                            />
                        )}

                        {/* Blog Content */}
                        <div className="p-4">
                            <h3 className="text-xl font-semibold text-gray-900">{blog.title}</h3>
                            <p className="text-gray-600 text-sm mb-2">By {blog.author}</p>
                            
                            {/* Blog Preview */}
                            <p className="text-gray-700 mb-4 line-clamp-3">
                                {blog.sections?.[0]?.paragraph ? blog.sections[0].paragraph.slice(0, 120) + "..." : "No content available."}
                            </p>

                            {/* Likes & Comments */}
                            <div className="flex justify-between items-center text-gray-600 text-sm">
                                <p>‚ù§Ô∏è {blog.likes || 0} Likes</p>
                                <p>üí¨ {blog.comments?.length || 0} Comments</p>
                            </div>

                            {/* Read More Button */}
                            <Link to={`/blogs/${blog._id}`} className="block mt-4 text-blue-600 font-semibold text-center hover:text-blue-700">
                                Read More ‚Üí
                            </Link>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
