import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import IMG from "../assets/logo (1).png";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  // Smooth scroll to contact section
  const scrollToSection = (e, sectionId) => {
    e.preventDefault();
    const section = document.getElementById(sectionId);
    if (section) {
      section.scrollIntoView({ behavior: "smooth" });
    }
  };
  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/">
          <img src={IMG} alt="Logo" />
        </Link>
      </div>

      {/* Hamburger Icon for Mobile Menu */}
      <div className="hamburger" onClick={toggleMenu}>
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>

      {/* Navbar Links */}
      <div className={`nav-links ${isMenuOpen ? "active" : ""}`}>
        <Link to="/">Home</Link>
        <a href="about"  onClick={scrollToContact}>About</a>
        <Link to="services" onClick={scrollToContact}>Services</Link>
        <Link to="/blogs">Blogs</Link> {/* Navigate to All Blogs Page */}
        <a href="#contact" onClick={scrollToContact}>Contact</a> {/* Smooth scroll */}
        <Link to="/login">Login</Link>
      </div>
    </nav>
  );
};

export default Navbar;
