import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get("http://localhost:5000/blogs")
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          setBlogs(response.data.slice(0, 2)); // Show only the first 2 blogs
        }
      })
      .catch((error) => console.error("Error fetching blogs:", error))
      .finally(() => setLoading(false));
  }, []);

  if (loading) {
    return <div className="text-center p-8 text-lg font-semibold">Loading blogs...</div>;
  }

  return (
    <div className="blogsec p-8 bg-gray-100">
      <h2 className="text-3xl font-bold text-center mb-6">Latest Blogs</h2>

      <div className="grid md:grid-cols-2 gap-6">
        {blogs.length > 0 ? (
          blogs.map((blog) => (
            <div key={blog.id} className="bg-white p-4 rounded-lg shadow-md">
              {blog.image && <img src={blog.image} alt={blog.title} className="w-full h-64 object-cover rounded-md" />}
              <div className="p-4">
                <p className="text-gray-600 text-sm">By <span className="font-semibold">{blog.author}</span> | {blog.date}</p>
                <h3 className="text-xl font-semibold mt-2">{blog.title}</h3>
                <p className="text-gray-700 mt-2">{blog.description ? blog.description.slice(0, 100) + "..." : "No description available."}</p>
                <Link to={`/blog/${blog.id}`} className="text-blue-500 font-semibold mt-4 inline-block">
                  Read More â†’
                </Link>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center col-span-2 text-gray-600">No blogs available.</p>
        )}
      </div>

      <div className="text-center mt-6">
        <Link to="/blogs" className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-md hover:bg-blue-600">
          Show More Blogs
        </Link>
      </div>
    </div>
  );
};

export default BlogSection;
