import React, { useState } from 'react';
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {
  const [activeBlog, setActiveBlog] = useState(null);

  // Function to handle clicking a blog post to view full content
  const handleBlogClick = (blogId) => {
    setActiveBlog(blogId); // This sets the active blog to show full content
  };

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        {/* Blog 1 */}
        <div className="blogs" onClick={() => handleBlogClick(1)}>
          <div className="bimg">
            <img src={IMG} alt="" />
          </div>
          <div className="bdesc">
            <p className="orther">
              BY <span>RAM</span> | Jun 18, 2015
            </p>
            <h1>Integer at diam gravida</h1>
            {activeBlog === 1 ? (
              <p>Full content of the blog goes here. This is a detailed description of the blog post. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam non urna vitae lectus sollicitudin sollicitudin.</p>
            ) : (
              <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nobis, dolor.</p>
            )}
            <button>Read More <i className="fa-solid fa-arrow-right"></i></button>
          </div>
        </div>

        {/* Blog 2 */}
        <div className="blogs" onClick={() => handleBlogClick(2)}>
          <div className="bimg">
            <img src={IMG2} alt="" />
          </div>
          <div className="bdesc">
            <p className="orther">
              BY <span>RAM</span> | Jun 18, 2015
            </p>
            <h1>Donec quis ex vel tincidunt</h1>
            {activeBlog === 2 ? (
              <p>Full content of the second blog post. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed auctor elit eu orci bibendum, sit amet dapibus leo consequat.</p>
            ) : (
              <p>Lorem, ipsum dolor sit amet consectetur adipisicing elit. Nobis, dolor.</p>
            )}
            <button>Read More <i className="fa-solid fa-arrow-right"></i></button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BlogSection;
