import React, { useState } from "react";
import axios from "axios";

const Login = () => {
  const [showSignup, setShowSignup] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [authData, setAuthData] = useState({ username: "", password: "" });

  // Handle input changes for authentication
  const handleAuthChange = (e) => {
    setAuthData({ ...authData, [e.target.name]: e.target.value });
  };
  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/signup", authData, {
        headers: { "Content-Type": "application/json" },
      });
      console.log(response.data);
      setIsLoggedIn(true);
    } catch (error) {
      console.error("Signup failed:", error.response?.data || error.message);
    }
  };
  
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:5000/api/login", authData, {
        headers: { "Content-Type": "application/json" },
      });
      console.log(response.data);
      localStorage.setItem("token", response.data.token);
      setIsLoggedIn(true);
    } catch (error) {
      console.error("Login failed:", error.response?.data || error.message);
    }
  };
  
  return (
    <div className="p-4 max-w-md mx-auto bg-white rounded-lg shadow-md">
      {showSignup ? (
        <form onSubmit={handleSignup}>
          <h2 className="text-xl font-bold mb-4">Sign Up</h2>
          <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} />
          <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} />
          <button type="submit" className="w-full bg-green-500 text-white p-2 rounded">Sign Up</button>
          <p className="mt-2 cursor-pointer text-blue-500" onClick={() => setShowSignup(false)}>Already have an account? Login</p>
        </form>
      ) : (
        <form onSubmit={handleLogin}>
          <h2 className="text-xl font-bold mb-4">Login</h2>
          <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} />
          <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} />
          <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Login</button>
        </form>
      )}
    </div>
  );
};

export default Login;
