import { useState } from 'react';
import axios from 'axios';

export default function BlogUpload() {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [content, setContent] = useState([]);
  const [image, setImage] = useState(null);

  const addSection = () => {
    setContent([...content, { heading: '', paragraph: '' }]);
  };

  const handleContentChange = (index, field, value) => {
    const updatedContent = [...content];
    updatedContent[index][field] = value;
    setContent(updatedContent);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('title', title);
    formData.append('author', author);
    formData.append('content', JSON.stringify(content));
    if (image) formData.append('image', image);

    try {
      await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      alert('Blog uploaded successfully!');
    } catch (error) {
      alert('Error uploading blog');
    }
  };

  return (
    <div className="max-w-2xl mx-auto p-6 bg-white shadow rounded-lg">
      <h2 className="text-xl font-bold mb-4">Upload a Blog</h2>
      <input
        type="text"
        placeholder="Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />
      <input
        type="text"
        placeholder="Author"
        value={author}
        onChange={(e) => setAuthor(e.target.value)}
        className="w-full p-2 border rounded mb-2"
      />
      {content.map((section, index) => (
        <div key={index} className="mb-2">
          <input
            type="text"
            placeholder="Heading"
            value={section.heading}
            onChange={(e) => handleContentChange(index, 'heading', e.target.value)}
            className="w-full p-2 border rounded mb-1"
          />
          <textarea
            placeholder="Paragraph"
            value={section.paragraph}
            onChange={(e) => handleContentChange(index, 'paragraph', e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
      ))}
      <button onClick={addSection} className="bg-blue-500 text-white p-2 rounded mb-2">Add Section</button>
      <input type="file" onChange={(e) => setImage(e.target.files[0])} className="mb-2" />
      <button onClick={handleSubmit} className="bg-green-500 text-white p-2 rounded">Upload</button>
    </div>
  );
}
