import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const BlogForm = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [image, setImage] = useState(null);
  const [sections, setSections] = useState([{ heading: "", paragraph: "" }]);
  const navigate = useNavigate();

  // Load author name from localStorage after the component mounts
  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setAuthor(storedUsername);
    }
  }, []);

  const handleSectionChange = (index, field, value) => {
    const updatedSections = [...sections];
    updatedSections[index][field] = value;
    setSections(updatedSections);
  };

  const addSection = () => {
    setSections([...sections, { heading: "", paragraph: "" }]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token"); // Get JWT token from localStorage

    if (!token) {
      alert("You must be logged in to upload a blog.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author); // Send the logged-in user's name
    if (image) formData.append("image", image);
    formData.append("sections", JSON.stringify(sections)); // Convert sections to JSON

    try {
      const response = await axios.post("http://localhost:5001/blogs", formData, {
        headers: {
          Authorization: `Bearer ${token}`, // Send token in headers
          "Content-Type": "multipart/form-data",
        },
      });

      alert("Blog uploaded successfully");
      navigate("/"); // Redirect to homepage
    } catch (error) {
      console.error("Upload Error:", error);
      alert(error.response?.data?.error || "Error uploading blog");
    }
  };

  return (
    <div className="p-6 max-w-lg mx-auto bg-white rounded shadow">
      <h2 className="text-xl font-bold mb-4">Upload Blog</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          className="border p-2 w-full mb-2"
          required
        />

        {/* Show Author Name Instead of Input Field */}
        <p className="mb-2 text-gray-700">
          Author: <strong>{author || "Loading..."}</strong>
        </p>

        <input
          type="file"
          accept="image/*"
          onChange={(e) => setImage(e.target.files[0])}
          className="border p-2 w-full mb-2"
          required
        />

        {sections.map((section, index) => (
          <div key={index} className="mb-4">
            <input
              type="text"
              placeholder="Heading"
              value={section.heading}
              onChange={(e) => handleSectionChange(index, "heading", e.target.value)}
              className="border p-2 w-full mb-2"
              required
            />
            <textarea
              placeholder="Paragraph"
              value={section.paragraph}
              onChange={(e) => handleSectionChange(index, "paragraph", e.target.value)}
              className="border p-2 w-full mb-2"
              required
            />
          </div>
        ))}

        <button
          type="button"
          onClick={addSection}
          className="bg-gray-300 px-4 py-2 rounded mb-2"
        >
          Add Section
        </button>

        <button type="submit" className="bg-blue-500 text-white px-4 py-2 rounded">
          Submit
        </button>
      </form>
    </div>
  );
};

export default BlogForm;
