import React, { useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:5000/api/signup", authData);
            setIsLoggedIn(true);
        } catch (error) {
            console.error("Signup failed", error);
        }
    };
    
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/api/login", authData);
            localStorage.setItem("token", response.data.token);
            setIsLoggedIn(true);
        } catch (error) {
            console.error("Login failed", error);
        }
    };
    
    const handleSubmit = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append("author", blogData.author);
        formData.append("title", blogData.title);
        formData.append("content", blogData.content);
        if (blogData.image) formData.append("image", blogData.image);
    
        try {
            await axios.post("http://localhost:5000/api/blogs", formData);
            alert("Blog added successfully");
        } catch (error) {
            console.error("Error adding blog", error);
        }
    };
    

    return (
        <div className="p-4 max-w-md mx-auto bg-white rounded-lg shadow-md">
            {showSignup ? (
                <form onSubmit={handleSignup}>
                    <h2 className="text-xl font-bold mb-4">Sign Up</h2>
                    <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} />
                    <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} />
                    <button type="submit" className="w-full bg-green-500 text-white p-2 rounded">Sign Up</button>
                    <p className="mt-2 cursor-pointer text-blue-500" onClick={() => setShowSignup(false)}>Already have an account? Login</p>
                </form>
            ) : !isLoggedIn ? (
                <form onSubmit={handleLogin}>
                    <h2 className="text-xl font-bold mb-4">Login</h2>
                    <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} />
                    <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} />
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Login</button>
                </form>
            ) : (
                <form onSubmit={handleSubmit}>
                    <h2 className="text-xl font-bold mb-4">Add Blog</h2>
                    <input className="w-full p-2 mb-2 border rounded" type="text" name="author" placeholder="Author" onChange={handleChange} />
                    <input className="w-full p-2 mb-2 border rounded" type="text" name="title" placeholder="Title" onChange={handleChange} />
                    <textarea className="w-full p-2 mb-2 border rounded" name="content" placeholder="Content" onChange={handleChange}></textarea>
                    <input className="w-full p-2 mb-2 border rounded" type="file" name="image" onChange={handleChange} />
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Submit</button>
                </form>
            )}
        </div>
    );
};

export default AdminPanel;
