import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const BlogList = () => {
    const [blogs, setBlogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const userId = localStorage.getItem("userId"); // Get logged-in user ID

    useEffect(() => {
        const fetchBlogs = async () => {
            try {
                const response = await fetch('http://localhost:5001/blogs');
                const data = await response.json();
                setBlogs(data);
            } catch (error) {
                console.error("Error fetching blogs:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchBlogs();
    }, []);

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this blog?")) return;
        
        try {
            await fetch(`http://localhost:5001/blogs/${id}`, {
                method: "DELETE",
                headers: { "Content-Type": "application/json" },
            });

            setBlogs(blogs.filter(blog => blog._id !== id));
            alert("Blog deleted successfully");
        } catch (error) {
            console.error("Error deleting blog:", error);
            alert("Failed to delete blog");
        }
    };

    if (loading) return <p className="text-center">Loading blogs...</p>;

    return (
        <div className="container mx-auto py-10">
            <h2 className="text-3xl font-bold mb-6 text-center">Blogs</h2>
            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
                {blogs.map(blog => (
                    <div key={blog._id} className="bg-white p-4 shadow rounded-lg">
                        <img src={`http://localhost:5001${blog.image}`} alt={blog.title} className="w-full h-48 object-cover rounded-lg" />
                        <h3 className="text-xl font-semibold mt-2">{blog.title}</h3>
                        <p className="text-gray-600 mt-1">By {blog.author}</p>
                        <Link to={`/blog/${blog._id}`} className="block mt-2 text-blue-500">Read More</Link>

                        {/* Show Delete Button ONLY if blog belongs to logged-in user */}
                        {userId === blog.authorId && (
                            <button onClick={() => handleDelete(blog._id)} className="mt-2 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600">
                                Delete
                            </button>
                        )}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default BlogList;
