import axios from "axios";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import './BlogSection.css';

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get("http://localhost:5001/blogs")
      .then((response) => {
        if (response.data && Array.isArray(response.data)) {
          // Sort by most likes and most comments
          const sortedBlogs = response.data.sort(
            (a, b) =>
              (b.likes || 0) + (b.comments?.length || 0) -
              ((a.likes || 0) + (a.comments?.length || 0))
          );
          setBlogs(sortedBlogs.slice(0, 2)); // Show top 2 blogs
        }
      })
      .catch((error) => console.error("Error fetching blogs:", error))
      .finally(() => setLoading(false));
  }, []);


  if (loading) {
    return <div className="text-center p-8 text-lg font-semibold">Loading blogs...</div>;
  }

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
  <h2 className="text-2xl font-bold text-center mb-6">Latest Blogs</h2>

  {blogs.length > 0 ? (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
      {blogs.map((blog) => (
        <div
          key={blog._id}
          className="bg-white shadow-md rounded-lg overflow-hidden transition-transform transform hover:scale-105 w-full max-w-[320px] mx-auto"
        >
          {/* Blog Image */}
          <div className="h-40 w-full">
            {blog.image && (
              <img
                src={`http://localhost:5001${blog.image}`}
                alt={blog.title}
                className="w-full h-full object-cover rounded-t-lg"
              />
            )}
          </div>

          {/* Blog Content */}
          <div className="p-4 flex flex-col h-[180px]">
            <p className="text-xs text-gray-500 mb-1">
              BY <span className="font-semibold">{blog.author}</span> |{" "}
              {blog.createdAt
                ? new Date(blog.createdAt).toLocaleDateString("en-US", {
                    year: "numeric",
                    month: "short",
                    day: "numeric",
                  })
                : "Date not available"}
            </p>

            <h1 className="text-lg font-semibold line-clamp-2">{blog.title}</h1>
            <p className="text-gray-700 text-sm mt-2 line-clamp-3">
              {blog.sections?.[0]?.paragraph
                ? blog.sections[0].paragraph.slice(0, 80) + "..."
                : "No description available."}
            </p>

            {/* Likes & Comments Count */}
            <div className="flex justify-between items-center text-gray-600 text-xs mt-auto">
              <p>❤️ {blog.likes || 0} Likes</p>
              <p>💬 {blog.comments?.length || 0} Comments</p>
            </div>

            <Link
              to={`/blogs/${blog._id}`}
              className="text-blue-500 font-semibold mt-3 inline-block"
            >
              Read More →
            </Link>
          </div>
        </div>
      ))}
    </div>
  ) : (
    <p className="text-center text-gray-600">No trending blogs available.</p>
  )}

  <div className="text-center mt-6">
    <Link
      to="/bloglist"
      className="bg-blue-500 text-white px-4 py-2 rounded-md shadow-md hover:bg-blue-600"
    >
      Show More Blogs
    </Link>
  </div>
</div>

      
    </div>

  );
};

export default BlogSection;
