import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";
import IMG from "../assets/logo (1).png";

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    setIsLoggedIn(!!token); // Convert token existence to boolean
  }, []);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    setIsLoggedIn(false);
    navigate("/");
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <Link to="/">
          <img src={IMG} alt="Logo" />
        </Link>
      </div>

      {/* Hamburger Icon for Mobile Menu */}
      <div className="hamburger" onClick={toggleMenu}>
        <div className="bar"></div>
        <div className="bar"></div>
        <div className="bar"></div>
      </div>

      {/* Navbar Links */}
      <div className={`nav-links ${isMenuOpen ? "active" : ""}`}>
        <Link to="/">Home</Link>
        <a href="#about" onClick={(e) => e.preventDefault()} className="text-blue-500">
          About
        </a>
        <a href="#services" onClick={(e) => e.preventDefault()} className="text-blue-500">
          Services
        </a>
        <Link to="/blogs">Blogs</Link>
        <a href="#contact" onClick={(e) => e.preventDefault()} className="text-blue-500">
          Contact
        </a>

        {/* Conditional Login / Logout Buttons */}
        {isLoggedIn ? (
          <button onClick={handleLogout} className="bg-red-500 px-4 py-2 rounded text-white">
            Logout
          </button>
        ) : (
          <>
            <Link to="/login" className=" text-white">
              Login
            </Link>
            <Link to="/register" className="bg-green-500 px-4 py-2 rounded text-white">
              Register
            </Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
