import { useState } from "react";
import axios from "axios";

const BlogForm = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [image, setImage] = useState(null);
  const [content, setContent] = useState([]);

  const handleAddContent = (type) => {
    setContent([...content, { type, value: "" }]);
  };

  const handleContentChange = (index, value) => {
    const newContent = [...content];
    newContent[index].value = value;
    setContent(newContent);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    formData.append("image", image);
    formData.append("content", JSON.stringify(content));

    try {
      const response = await axios.post("http://localhost:5001/upload-blog", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Blog uploaded successfully!");
      console.log(response.data);
    } catch (error) {
      console.error("Error uploading blog:", error);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-5 bg-white shadow-lg rounded-lg mt-5">
      <h2 className="text-2xl font-semibold mb-4">Create a New Blog</h2>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Blog Title"
          className="w-full p-2 border rounded"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Author Name"
          className="w-full p-2 border rounded"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
          required
        />
        <input
          type="file"
          className="w-full p-2 border rounded"
          onChange={(e) => setImage(e.target.files[0])}
          accept="image/*"
        />

        {/* Dynamic Content Fields */}
        {content.map((item, index) => (
          <div key={index} className="flex items-center space-x-2">
            <input
              type="text"
              placeholder={`Enter ${item.type}`}
              className="w-full p-2 border rounded"
              value={item.value}
              onChange={(e) => handleContentChange(index, e.target.value)}
              required
            />
            <span className="text-gray-500">{item.type}</span>
          </div>
        ))}

        {/* Buttons to Add Content */}
        <div className="flex space-x-2">
          <button
            type="button"
            className="bg-blue-500 text-white px-4 py-2 rounded"
            onClick={() => handleAddContent("heading")}
          >
            + Add Heading
          </button>
          <button
            type="button"
            className="bg-green-500 text-white px-4 py-2 rounded"
            onClick={() => handleAddContent("paragraph")}
          >
            + Add Paragraph
          </button>
        </div>

        <button type="submit" className="w-full bg-purple-500 text-white p-2 rounded">
          Submit Blog
        </button>
      </form>
    </div>
  );
};

export default BlogForm;
