import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminPanel = () => {
    const [formData, setFormData] = useState({
        author: '',
        title: '',
        excerpt: '',
        content: '',
        image: null
    });

    const [authData, setAuthData] = useState({
        username: '',
        password: ''
    });

    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [showSignup, setShowSignup] = useState(true); // Start with Signup Form

    // Check if user is logged in
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            setIsLoggedIn(true);
        }
    }, []);

    // Handle input changes for signup/login form
    const handleAuthChange = (e) => {
        setAuthData({ ...authData, [e.target.name]: e.target.value });
    };

    // Handle signup
    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:6000/register', authData);
            alert('Signup successful! Please login.');
            setShowSignup(false); // Switch to Login Form
        } catch (error) {
            console.error(error);
            alert('Signup failed. Try a different username.');
        }
    };

    // Handle login
    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:6000/login', authData);
            localStorage.setItem('token', response.data.token);
            setIsLoggedIn(true);
        } catch (error) {
            console.error(error);
            alert('Login failed. Check your credentials.');
        }
    };

    // Handle logout
    const handleLogout = () => {
        localStorage.removeItem('token');
        setIsLoggedIn(false);
    };

    // Handle input changes for blog form
    const handleChange = (e) => {
        if (e.target.name === 'image') {
            setFormData({ ...formData, image: e.target.files[0] });
        } else {
            setFormData({ ...formData, [e.target.name]: e.target.value });
        }
    };

    // Handle blog form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Unauthorized');
            return;
        }

        const formDataObj = new FormData();
        for (const key in formData) {
            formDataObj.append(key, formData[key]);
        }

        try {
            await axios.post('http://localhost:6000/add-blog', formDataObj, {
                headers: { 
                    Authorization: token,
                    'Content-Type': 'multipart/form-data'
                }
            });
            alert('Blog added successfully!');
        } catch (error) {
            console.error(error);
            alert('Error adding blog');
        }
    };

    return (
        <div className="p-6 max-w-lg mx-auto bg-white rounded-lg shadow-md mt-10">
            {!isLoggedIn ? (
                showSignup ? (
                    // Signup Form
                    <div>
                        <h2 className="text-2xl font-bold mb-4 text-center">Admin Signup</h2>
                        <form onSubmit={handleSignup}>
                            <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} required />
                            <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} required />
                            <button type="submit" className="w-full bg-green-500 text-white p-2 rounded">Sign Up</button>
                        </form>
                        <p className="text-center mt-2">Already have an account? <button onClick={() => setShowSignup(false)} className="text-blue-500">Login</button></p>
                    </div>
                ) : (
                    // Login Form
                    <div>
                        <h2 className="text-2xl font-bold mb-4 text-center">Admin Login</h2>
                        <form onSubmit={handleLogin}>
                            <input className="w-full p-2 mb-2 border rounded" type="text" name="username" placeholder="Username" onChange={handleAuthChange} required />
                            <input className="w-full p-2 mb-2 border rounded" type="password" name="password" placeholder="Password" onChange={handleAuthChange} required />
                            <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Login</button>
                        </form>
                        <p className="text-center mt-2">Don't have an account? <button onClick={() => setShowSignup(true)} className="text-blue-500">Sign Up</button></p>
                    </div>
                )
            ) : (
                // Blog Form (Visible Only When Logged In)
                <div>
                    <h2 className="text-2xl font-bold mb-4 text-center">Add Blog</h2>
                    <form onSubmit={handleSubmit}>
                        <input className="w-full p-2 mb-2 border rounded" type="text" name="author" placeholder="Author" onChange={handleChange} required />
                        <input className="w-full p-2 mb-2 border rounded" type="text" name="title" placeholder="Title" onChange={handleChange} required />
                        <input className="w-full p-2 mb-2 border rounded" type="text" name="excerpt" placeholder="Excerpt" onChange={handleChange} required />
                        <textarea className="w-full p-2 mb-2 border rounded" name="content" placeholder="Content" onChange={handleChange} required></textarea>
                        <input className="w-full p-2 mb-2 border rounded" type="file" name="image" onChange={handleChange} required />
                        <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Submit</button>
                    </form>
                    <button onClick={handleLogout} className="w-full bg-red-500 text-white p-2 mt-4 rounded">Logout</button>
                </div>
            )}
        </div>
    );
};

export default AdminPanel;
