import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import Blogsec from './components/BlogSection';
import BlogPost from './components/BlogPost'; // Import the BlogPost component

const App = () => {
  return (
    <Router>
      <div>
        <Navbar />
        <Hero />
        <Blogsec /> {/* Blog section that shows the list of blogs */}

        {/* Add route for the standalone blog post page */}
        <Routes>
          <Route path="/blog/:id" element={<BlogPost />} /> {/* Specific blog page */}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
