import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const Blogs = () => {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/blogs")
      .then((response) => setBlogs(response.data))
      .catch((error) => console.error("Error fetching blogs:", error));
  }, []);

  return (
    <div className="max-w-6xl mx-auto p-4">
      <h2 className="text-3xl font-bold text-center mb-6">All Blogs</h2>

      {blogs.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {blogs.map((blog) => (
            <div key={blog._id} className="bg-white border border-gray-200 rounded-lg shadow-sm overflow-hidden">
              {blog.image && (
                <img 
                  src={`http://localhost:5000${blog.image}`} 
                  alt={blog.title} 
                  className="w-full h-48 object-cover"
                />
              )}
              <div className="p-5">
                <h5 className="mb-2 text-xl font-bold text-gray-900">{blog.title}</h5>
                <p className="mb-3 text-gray-700">{blog.content.substring(0, 100)}...</p>
                <Link to={`/blog/${blog._id}`} className="text-blue-500 font-semibold flex items-center">
                  Read More
                  <svg className="w-4 h-4 ml-2" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 14 10">
                    <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M1 5h12m0 0L9 1m4 4L9 9" />
                  </svg>
                </Link>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-gray-600">No blogs available.</p>
      )}
    </div>
  );
};

export default Blogs;
