import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const BlogPost = () => {
  const { id } = useParams(); // Get the ID of the blog from the URL
  const [blog, setBlog] = useState(null);

  useEffect(() => {
    // Fetch the blog data from your API or mock data based on the ID
    const fetchBlog = async () => {
      try {
        const response = await fetch(`http://localhost:5000/blogs/${id}`);
        const data = await response.json();
        setBlog(data); // Store the blog data
      } catch (error) {
        console.error("Error fetching the blog:", error);
      }
    };
    fetchBlog();
  }, [id]);

  if (!blog) {
    return <div>Loading...</div>; // Show loading message until blog is fetched
  }

  return (
    <div style={{ padding: '20px' }}>
      <h1>{blog.title}</h1>
      <p><strong>{blog.author}</strong> | {blog.date}</p>
      <img src={blog.image} alt={blog.title} style={{ width: '100%' }} />
      <p>{blog.content}</p> {/* Display the full blog content */}
    </div>
  );
};

export default BlogPost;
