// BlogPostStandalone.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const BlogPostStandalone = () => {
  const { id } = useParams(); // Extract the 'id' from the URL
  const [blog, setBlog] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const response = await fetch(`http://localhost:5000/blogs/${id}`);
        const data = await response.json();
        setBlog(data); // Set the full blog data
      } catch (error) {
        console.error('Error fetching blog content:', error);
      }
    };

    fetchBlog();
  }, [id]);

  if (!blog) {
    return <p>Loading...</p>; // Display a loading message if the blog data is not available
  }

  return (
    <div className="blogpost-standalone">
      <h1>{blog.title}</h1>
      <p>BY <span>{blog.author}</span> | {blog.date}</p>
      <div className="bimg">
        <img src={blog.image} alt={blog.title} />
      </div>
      <p>{blog.content}</p> {/* Show the full content of the blog */}
    </div>
  );
};

export default BlogPostStandalone;
