import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const BlogList = () => {
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        const response = await fetch("http://localhost:5001/blogs");
        if (!response.ok) throw new Error("Failed to fetch blogs");
        const data = await response.json();
        setBlogs(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchBlogs();
  }, []);

  if (loading) return <p className="text-center text-gray-500">Loading blogs...</p>;
  if (error) return <p className="text-center text-red-500">{error}</p>;
  if (blogs.length === 0) return <p className="text-center text-gray-500">No blogs found.</p>;

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-6">
      <h1 className="text-3xl font-bold text-center mb-8">Latest Blogs</h1>
      <div className="max-w-6xl mx-auto grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {blogs.map((blog) => (
          <div key={blog._id} className="bg-white p-4 rounded-lg shadow-md">
            <img
              src={`http://localhost:5001${blog.image}`}
              alt={blog.title}
              className="w-full h-48 object-cover rounded-lg"
            />
            <h2 className="text-xl font-bold mt-3">{blog.title}</h2>
            <p className="text-gray-600 text-sm mt-1">By {blog.author} • {new Date(blog.date).toDateString()}</p>
            <p className="text-gray-700 mt-2 line-clamp-3">{blog.description}</p>
            <div className="mt-4 flex justify-between items-center">
              <span className="text-red-500">❤️ {blog.likes || 0} Likes</span>
              <Link to={`/blogs/${blog._id}`} className="text-blue-500 font-semibold mt-4 inline-block">
                               Read More →
                             </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default BlogList;
