import React, { useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
    const [formData, setFormData] = useState({
        date: '',
        author: '',
        title: '',
        excerpt: '',
        content: '',
        image: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            alert('Unauthorized');
            return;
        }
        try {
            await axios.post('http://localhost:5000/add-blog', formData, {
                headers: { Authorization: token }
            });
            alert('Blog added successfully!');
        } catch (error) {
            console.error(error);
            alert('Error adding blog');
        }
    };

    return (
        <div className="p-4 max-w-md mx-auto bg-white rounded-lg shadow-md">
            <h2 className="text-xl font-bold mb-4">Add Blog</h2>
            <form onSubmit={handleSubmit}>
                {Object.keys(formData).map((key) => (
                    <input key={key} className="w-full p-2 mb-2 border rounded" type="text" name={key} placeholder={key} onChange={handleChange} />
                ))}
                <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Submit</button>
            </form>
        </div>
    );
};

export default AdminPanel;
