import axios from "axios";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {

  const [blogs, setBlogs] = useState([]);
  
    useEffect(() => {
      axios.get("http://localhost:5000/blogs").then((response) => {
        setBlogs(response.data.slice(0, 2));
      });
    }, []);

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
       <div className="">
      <h2 className="">Latest Blogs</h2>
      <div className="">
        {blogs.map((blog) => (
          <div key={blog.id} className="">
            <img src={blog.image} alt={blog.title} className="" />
            <h3 className="">{blog.title}</h3>
            <p className="">{blog.excerpt}</p>
            <Link to={`/blog/${blog.id}`} className="">Read More</Link>
          </div>
        ))}
      </div>
      <div className="">
        <Link to="/blogs" className="">Show More Blogs</Link>
      </div>
    </div>
      </div>
      {/* <div className="">
        <Link to="/blogs" className="">Show More Blogs</Link>
      </div> */}
    </div>
  );
};









export default BlogSection;
