import { useState } from "react";

const BlogForm = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [image, setImage] = useState(null);
  const [content, setContent] = useState([]); // Stores headings & paragraphs
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const handleImageChange = (e) => setImage(e.target.files[0]);

  // Add a new heading
  const addHeading = () => {
    setContent([...content, { type: "heading", value: "" }]);
  };

  // Add a new paragraph
  const addParagraph = () => {
    setContent([...content, { type: "paragraph", value: "" }]);
  };

  // Update the value of a heading/paragraph
  const updateContent = (index, value) => {
    const updatedContent = [...content];
    updatedContent[index].value = value;
    setContent(updatedContent);
  };

  // Remove a heading/paragraph
  const removeContent = (index) => {
    const updatedContent = content.filter((_, i) => i !== index);
    setContent(updatedContent);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    const token = localStorage.getItem("token");
    if (!token) {
      setError("You must be logged in to submit a blog.");
      return;
    }

    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    formData.append("image", image);
    formData.append("content", JSON.stringify(content)); // Send content as JSON string

    try {
      const response = await fetch("http://localhost:5001/upload-blog", {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` }, // Send auth token
        body: formData, // Send form data with image
      });

      const data = await response.json();
      if (response.ok) {
        setSuccess("Blog uploaded successfully!");
        setTitle("");
        setAuthor("");
        setImage(null);
        setContent([]);
      } else {
        setError(data.error || "Failed to upload blog.");
      }
    } catch (err) {
      setError("Error submitting blog.");
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100 p-6">
      <div className="bg-white p-8 rounded-lg shadow-md w-full max-w-2xl">
        <h2 className="text-2xl font-bold mb-6 text-center">Upload Blog</h2>
        {error && <p className="text-red-500 text-sm mb-4">{error}</p>}
        {success && <p className="text-green-500 text-sm mb-4">{success}</p>}
        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="block text-gray-700">Title</label>
            <input
              type="text"
              className="w-full p-2 border rounded mt-1"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Author</label>
            <input
              type="text"
              className="w-full p-2 border rounded mt-1"
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700">Image</label>
            <input
              type="file"
              accept="image/*"
              className="w-full p-2 border rounded mt-1"
              onChange={handleImageChange}
              required
            />
          </div>

          {/* Dynamic Heading and Paragraphs */}
          {content.map((item, index) => (
            <div key={index} className="mb-4">
              {item.type === "heading" ? (
                <div>
                  <label className="block text-gray-700">Heading</label>
                  <input
                    type="text"
                    className="w-full p-2 border rounded mt-1"
                    value={item.value}
                    onChange={(e) => updateContent(index, e.target.value)}
                    required
                  />
                </div>
              ) : (
                <div>
                  <label className="block text-gray-700">Paragraph</label>
                  <textarea
                    className="w-full p-2 border rounded mt-1"
                    value={item.value}
                    onChange={(e) => updateContent(index, e.target.value)}
                    required
                  ></textarea>
                </div>
              )}
              <button
                type="button"
                className="text-red-500 text-sm mt-2"
                onClick={() => removeContent(index)}
              >
                Remove
              </button>
            </div>
          ))}

          {/* Add Buttons */}
          <div className="flex space-x-2">
            <button
              type="button"
              className="bg-blue-500 text-white px-3 py-1 rounded"
              onClick={addHeading}
            >
              + Add Heading
            </button>
            <button
              type="button"
              className="bg-green-500 text-white px-3 py-1 rounded"
              onClick={addParagraph}
            >
              + Add Paragraph
            </button>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded hover:bg-blue-600 mt-4"
          >
            Submit Blog
          </button>
        </form>
      </div>
    </div>
  );
};

export default BlogForm;
