import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BlogDetail = () => {
    const { id } = useParams();
    const [blog, setBlog] = useState(null);
    const [comment, setComment] = useState('');
    const [liked, setLiked] = useState(false);
    const [likes, setLikes] = useState(0);

    useEffect(() => {
        axios.get(`http://localhost:5001/blogs/${id}`)
            .then(response => {
                setBlog(response.data);
                setLikes(response.data.likes.length);
            })
            .catch(error => console.error('Error fetching blog:', error));
    }, [id]);

    const handleLike = async () => {
        const userId = localStorage.getItem("userId") || "guest"; // Get user ID
      
        try {
          const response = await fetch(`http://localhost:5001/blogs/${id}/like`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ userId }), // Send user ID
          });
      
          const data = await response.json();
          if (!response.ok) throw new Error(data.error || "Failed to like blog");
      
          setBlog({ ...blog, likes: data.likes });
        } catch (error) {
          console.error("Like Error:", error);
          alert(error.message);
        }
      };

    const handleComment = () => {
        const user = "John Doe"; // Replace with actual user name from authentication
        axios.post(`http://localhost:5001/blogs/${id}/comment`, { user, text: comment })
            .then(response => {
                setBlog({ ...blog, comments: response.data });
                setComment('');
            })
            .catch(error => console.error('Error adding comment:', error));
    };

    if (!blog) return <p>Loading...</p>;

    return (
        <div className="max-w-3xl mx-auto p-6">
            <h1 className="text-3xl font-bold">{blog.title}</h1>
            <p className="text-gray-600">By {blog.author}</p>
            {blog.image && <img src={`http://localhost:5001${blog.image}`} alt={blog.title} className="w-full my-4" />}
            {blog.sections.map((section, index) => (
                <div key={index} className="my-4">
                    <h2 className="text-xl font-semibold">{section.heading}</h2>
                    <p className="text-gray-700">{section.paragraph}</p>
                </div>
            ))}
            <div className="flex items-center mt-4">
                <button 
                    onClick={handleLike} 
                    className={`px-4 py-2 ${liked ? 'bg-red-500' : 'bg-gray-300'} text-white rounded-lg`}
                >
                    {liked ? 'Unlike' : 'Like'} ({likes})
                </button>
            </div>
            <div className="mt-6">
                <h3 className="text-xl font-bold">Comments</h3>
                <ul className="mt-2">
                    {blog.comments.map((c, index) => (
                        <li key={index} className="border-b py-2">
                            <strong>{c.user}:</strong> {c.text}
                        </li>
                    ))}
                </ul>
                <div className="mt-4">
                    <textarea
                        className="w-full p-2 border rounded"
                        placeholder="Write a comment..."
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                    />
                    <button onClick={handleComment} className="bg-blue-500 text-white px-4 py-2 rounded mt-2">
                        Post Comment
                    </button>
                </div>
            </div>
        </div>
    );
};

export default BlogDetail;
