import React, { useState } from 'react';
import './Brain.css';
import IMG from '../assets/services-img.png'

function Brain() {
    const [hoveredDotText, setHoveredDotText] = useState('');

    const dots = [
        { id: 1, color: '#FF5733', text: 'Creativity' },
        { id: 2, color: '#33FF57', text: 'Memory' },
        { id: 3, color: '#3357FF', text: 'Emotion' },
        { id: 4, color: '#F1C40F', text: 'Reasoning' },
        { id: 5, color: '#9B59B6', text: 'Language' },
        { id: 6, color: '#1ABC9C', text: 'Problem Solving' },
        { id: 7, color: '#E74C3C', text: 'Attention' },
        { id: 8, color: '#F39C12', text: 'Movement' },
    ];

    const handleDotHover = (text) => {
        setHoveredDotText(text);
    };

    const handleDotLeave = () => {
        setHoveredDotText('');
    };

    return (
        <div className="circle-container">
            <div className="leftdata"></div>
            <div className="circle">
                {dots.map((dot, index) => {
                    const angle = (index * 46); // Divide the circle into 8 sections
                    return (
                        <div
                            key={dot.id}
                            className="dot"
                            style={{
                                backgroundColor: dot.color,
                                transform: `rotate(${angle}deg) translate(0, -225px)`,
                                transformOrigin: 'center center',
                            }}
                            onMouseEnter={() => handleDotHover(dot.text)}
                            onMouseLeave={handleDotLeave}
                        />
                    );
                })}
                <img src={IMG} alt="Brain" className="brain-img" />
            </div>
            {hoveredDotText && <div className="text-overlay">{hoveredDotText}</div>}

            <div className="rightdata"></div>
        </div>
    );
}

export default Brain;
