import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const BlogDetail = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/blogs/${id}`)
      .then((response) => setBlog(response.data))
      .catch((error) => console.error("Error fetching blog:", error))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading)
    return <p className="text-center text-lg text-gray-500 animate-pulse">Loading...</p>;

  if (!blog)
    return <p className="text-center text-lg text-red-500">Blog not found.</p>;

  return (
    <div className="max-w-full mx-auto p-6 bg-white shadow-lg rounded-lg dark:bg-gray-900">
      <h1 className="text-4xl font-extrabold text-gray-900 dark:text-white mb-4">
        {blog.title}
      </h1>
      <p className="text-gray-600 dark:text-gray-400 text-sm mb-6">By {blog.author}</p>
      {blog.image && (
        <img
          src={`http://localhost:5000${blog.image}`}
          alt={blog.title}
          className="w-full h-auto rounded-lg shadow-md mb-6"
        />
      )}
      <p className="text-lg text-gray-800 dark:text-gray-300 leading-relaxed">{blog.content}</p>
    </div>
  );
};

export default BlogDetail;
