import axios from "axios";
import React, { useState, useEffect } from "react";
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {

  const [blogs, setBlogs] = useState([]);
  
    useEffect(() => {
      axios.get("http://localhost:5000/blogs").then((response) => {
        setBlogs(response.data.slice(0, 3));
      });
    }, []);

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        {blogs.map((blog) => (
          <div key={blog.id} className="blogs">
            <div className="bimg">
              <img src={require(`../assets/${blog.image}`)} alt={blog.title} />
            </div>
            <div className="bdesc">
              <p className="orther">
                BY <span>{blog.author}</span> | {blog.date}
              </p>
              <h1>{blog.title}</h1>
              <p>{blog.excerpt}</p>
              <Link to={`/blog/${blog.id}`} className="">Read More</Link>
            </div>
          </div>
        ))}
      </div>
      <div className="">
        <Link to="/blogs" className="">Show More Blogs</Link>
      </div>
    </div>
  );
};









export default BlogSection;
