import React, { useState,use } from 'react';
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {
  const [blogs, setBlogs] = useState([]);
  const [selectedBlog, setSelectedBlog] = useState(null);

  // Fetch blogs from JSON Server
  useEffect(() => {
    fetch("http://localhost:5000/blogs")
      .then((response) => response.json())
      .then((data) => setBlogs(data))
      .catch((error) => console.error("Error fetching blogs:", error));
  }, []);

  // Open blog in popup
  const openPopup = (blog) => {
    setSelectedBlog(blog);
  };

  // Close popup
  const closePopup = () => {
    setSelectedBlog(null);
  };
  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
        {blogs.map((blog) => (
          <div key={blog.id} className="blogs">
            <div className="bimg">
              <img src={require(`../assets/${blog.image}`)} alt={blog.title} />
            </div>
            <div className="bdesc">
              <p className="orther">
                BY <span>{blog.author}</span> | {blog.date}
              </p>
              <h1>{blog.title}</h1>
              <p>{blog.excerpt}</p>
              <button className="read-more-btn" onClick={() => openPopup(blog)}>
                Read More <i className="fa-solid fa-arrow-right"></i>
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Popup Modal */}
      {selectedBlog && (
        <div className="popup">
          <div className="popup-content">
            <span className="close-btn" onClick={closePopup}>&times;</span>
            <div className="popup-img">
              <img src={require(`../assets/${selectedBlog.image}`)} alt={selectedBlog.title} />
            </div>
            <h1>{selectedBlog.title}</h1>
            <p className="orther">
              BY <span>{selectedBlog.author}</span> | {selectedBlog.date}
            </p>
            <p>{selectedBlog.content}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default BlogSection;
