import axios from "axios";
import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useParams } from "react-router-dom";
import './BlogSection.css';
import IMG from '../assets/parallax-2.jpg';
import IMG2 from '../assets/parallax-3.jpg';

const BlogSection = () => {

  const [blogs, setBlogs] = useState([]);
  
    useEffect(() => {
      axios.get("http://localhost:5000/blogs").then((response) => {
        setBlogs(response.data.slice(0, 2));
      });
    }, []);

  return (
    <div className='blogsec'>
      <div className="blogleftsec">
        {/* Left section (if needed) */}
      </div>

      <div className="blogrightsec">
       <div className="">
      <h2 className="">Latest Blogs</h2>



      <div className="blogs">
        {blogs.map((blog) => (
          <div key={blog.id} className="blogsms">
            <img className="img" src="https://plus.unsplash.com/premium_photo-1661580574627-9211124e5c3f?q=80&w=1374&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D" alt={blog.title}  />
           <div className="dscbl"><h3 className="">{blog.title}</h3>
            <p className="">{blog.excerpt}</p>
            <Link to={`/blog/${blog.id}`} className="">Read More</Link></div> 
          </div>
        ))}
      </div>


      <div className="">
        <Link to="/blogs" className="">Show More Blogs</Link>
      </div>
    </div>
      </div>
      {/* <div className="">
        <Link to="/blogs" className="">Show More Blogs</Link>
      </div> */}
    </div>
  );
};









export default BlogSection;
