import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const BlogDetail = () => {
    const { id } = useParams();
    const [blog, setBlog] = useState(null);
    const [newComment, setNewComment] = useState("");
    const [username, setUsername] = useState("Guest"); // 


    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await axios.get(`http://localhost:5001/blogs/${id}`);
                setBlog(response.data);
                
            } catch (error) {
                console.error('Error fetching blog:', error);
            }
        };
        fetchBlog();
    }, [id]);

    const handleLike = async () => {
        const userId = localStorage.getItem("userId") || "guest"; // Get user ID
      
        try {
          const response = await fetch(`http://localhost:5001/blogs/${id}/like`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ userId }), // Send user ID
          });
      
          const data = await response.json();
          if (!response.ok) throw new Error(data.error || "Failed to like blog");
      
          setBlog({ ...blog, likes: data.likes });
        } catch (error) {
          console.error("Like Error:", error);
          alert(error.message);
        }
      };
      
      
      
    
      const handleComment = async () => {
        if (!newComment.trim()) return;
        try {
          const response = await fetch(`http://localhost:5001/blogs/${id}/comment`, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ username, text: newComment }),
          });
          const data = await response.json();
          if (response.ok) {
            setBlog({ ...blog, comments: data.comments });
            setNewComment("");
          }
        } catch {
          alert("Failed to add comment");
        }
      };

   

    if (!blog) return <p>Loading...</p>;

    return (
        <div className="p-6 max-w-3xl mx-auto">
            <h2 className="text-2xl font-bold mb-2">{blog.title}</h2>
            <p className="text-gray-600 mb-4">By {blog.author}</p>
            {blog.image && <img src={`http://localhost:5001${blog.image}`} alt={blog.title} className="w-full h-64 object-cover rounded" />}
            {blog.sections.map((section, index) => (
                <div key={index} className="mt-4">
                    <h3 className="text-lg font-semibold">{section.heading}</h3>
                    <p>{section.paragraph}</p>
                </div>
            ))}
           
                   {/* Like Button */}
        <button onClick={handleLike} className="mt-4 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600 transition">
          ❤️ {blog.likes || 0} Likes
        </button>

        {/* Comments Section */}
        <div className="mt-6">
          <h3 className="text-xl font-bold">Comments</h3>
          <div className="mt-2">
            {blog.comments && blog.comments.length > 0 ? (
              blog.comments.map((comment, index) => (
                <div key={index} className="border-b border-gray-300 py-2">
                  <p className="text-sm text-gray-600">{comment.username} • {new Date(comment.date).toLocaleDateString()}</p>
                  <p className="text-gray-800">{comment.text}</p>
                </div>
              ))
            ) : (
              <p className="text-gray-500">No comments yet.</p>
            )}
          </div>
        </div>

        {/* Add Comment */}
        <div className="mt-4">
          <input
            type="text"
            placeholder="Your name"
            className="border p-2 w-full rounded mb-2"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <textarea
            placeholder="Write a comment..."
            className="border p-2 w-full rounded"
            value={newComment}
            onChange={(e) => setNewComment(e.target.value)}
          />
          <button onClick={handleComment} className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition">
            Add Comment
          </button>
        </div>

        </div>
    );
};

export default BlogDetail;
